<template>  <div>    <el-table        :data="tableData"        border        style="width: 50%">      <el-table-column          fixed          prop="id"          label="编号"          width="150">      </el-table-column>      <el-table-column          prop="name"          label="书名"          width="120">      </el-table-column>      <el-table-column          prop="author"          label="作者"          width="120">      </el-table-column>      <el-table-column          fixed="right"          label="操作"          width="200">        <template slot-scope="scope">          <el-button @click="handleClick(scope.row)" type="text" size="small">查看</el-button>          <el-button type="text" size="small">编辑</el-button>        </template>      </el-table-column>    </el-table>    <el-pagination        background        layout="prev, pager, next"        page-size="6"        :total=total    @current-change="page">    </el-pagination>  </div></template><script>export default {  methods: {    handleClick(row) {      console.log(row);    },    page(currentPage) {      const _this = this      axios.get('http://localhost:8081/book/'+ currentPage +'/6').then(function (resp){        _this.tableData = resp.data.list        _this.total = resp.data.total      })    }  },  created() {    const _this = this    axios.get('http://localhost:8081/book/1/6').then(function (resp){      _this.tableData = resp.data.list      _this.total = resp.data.total    })  },  data() {    return {      total: null,      tableData: null    }  }}</script>